mapper.insert:
insert into edit_bill (operationDate,registrationNumber, price, 
  realPay, note,accountType,firmType,modifyTime,registrationTime,company,sourceTo,
  
    sourceId,account
  
  
    businessesId
  
  )
values (#{operationDate}, #{registrationNumber}, #{price}, 
  #{realPay}, #{note},#{accountType},#{firmType},#{modifyTime},#{registrationTime},#{company},#{sourceTo},
  
    #{sourceId},#{account}
  
  
    #{businessesId}
  
  )

mapper.update:
update edit_bill set operationDate = #{oDatetimestamps}, account = #{account}, registrationNumber = #{registrationNumber}, price = #{price}, realPay = #{realPay}, note = #{note}, accountType = #{accountType}, firmType = #{firmType}, modifyTime = #{modifyTimestamps}, registrationTime = #{registrationTimestamps}, sourceTo = #{sourceTo},
sourceId= #{sourceId}, account = #{account}
businessesId = #{businessesId}
where id = #{id}

mapper.select:
select id,operationDate,account,registrationNumber,price,realPay,note,accountType,firmType,sourceId,registrationTime,sourceTo from edit_bill where deleted='0' and company = #{company} and firmType =#{firmType} and (
account = #{account} or
1=2) order by operationDate desc limit #{page},#{pageSize}
