mapper.select:
select count(phone) from user where
registrationTime>=#{todayZeroTimestamps}
and registrationTime <
#{tomorrowZeroTimestamps}

select count(phone) from user where
registrationTime>=#{monthlyZeroTimestamps} and registrationTime <
#{nextMonthlyZeroTimestamps}

mapper.update:
update user
set loginStatus = #{loginStatus},
openId = #{openId}
where phone = #{phone}

update user
set loginStatus = #{loginStatus}
where phone = #{phone}

update user
set loginStatus = #{loginStatus}
where id = #{userId}
